# quelques lignes qui appellent les clés d'API de ton fichier .env
def configure_rest_client 
  rest_client = Twitter::REST::Client.new do |config| #You can use this client to make any Twitter REST API request.
    config.consumer_key        = ENV["TWITTER_CONSUMER_KEY"]
    config.consumer_secret     = ENV["TWITTER_CONSUMER_SECRET"]
    config.access_token        = ENV["TWITTER_ACCESS_TOKEN"]
    config.access_token_secret = ENV["TWITTER_ACCESS_TOKEN_SECRET"]
  end
end

def configure_streaming_client 
  streaming_client = Twitter::Streaming::Client.new do |config| #You can use this client to make any Twitter Streaming API request.
    config.consumer_key        = ENV["TWITTER_CONSUMER_KEY"]
    config.consumer_secret     = ENV["TWITTER_CONSUMER_SECRET"]
    config.access_token        = ENV["TWITTER_ACCESS_TOKEN"]
    config.access_token_secret = ENV["TWITTER_ACCESS_TOKEN_SECRET"]
  end
end

