#1. say "hello" à 5 journalistes choisis au hasard parmi l'array
def hello_random(rest_client)
  five_random_journalists = journalists.sample(5)
  five_random_journalists.each do |journalist_tweeter|
   rest_client.update("Hello #{journalist_tweeter}! Nice job ! #bonjour_monde @the_hacking_pro")
   puts "The bot sent a tweet to #{journalist_tweeter}"
  end
end

#2. Liker les 25 derniers tweets qui ont un #bonjour_monde
def like_tweet(rest_client)
  rest_client.search("#bonjour_monde", result_type: "recent").take(25).collect do |tweet|
    rest_client.favorite(tweet)
    puts "The bot liked that tweet : #{tweet.text}"
  end
end

#3. Follow les 20 dernières personnes qui ont tweeté avec #bonjour_monde
def follow_people(rest_client)
  rest_client.search('#bonjour_monde').take(20).uniq.each do |tweet|
    if (tweet.user != client.user)
      rest_client.follow(tweet.user)
      puts "The bot followed @#{tweet.user.screen_name}"
    end
  end
end

#4. Utiliser la fonction Streaming de l'API Twitter afin de réagir en live (like et follow des comptes tweetant le #bonjour_monde)
def live_follow_and_like(rest_client, streaming_client)
  streaming_client.filter(track: "#bonjour_monde") do |tweet|
    if tweet.is_a?(Twitter::Tweet) #Check if received tweet is an original tweet (autre exemple pour checker des messages reçus : Twitter::DirectMessage) 
        rest_client.favorite(tweet)
        rest_client.follow(tweet.user.id)
        puts "The bot liked that tweet : #{tweet.text} and followed that tweeter : #{tweet.user.id}"
    end
  end
end

